import React from "react";
import {
   ButtonDropdown,
   DropdownToggle,
   DropdownMenu,
   DropdownItem
} from "reactstrap";

class ButtonsDropdown extends React.Component {
    constructor(props){
        super(props);
        

     
    }

   state = {
      dropdownOpen: false
   };

   toggle = () => {
      this.setState({
         dropdownOpen: !this.state.dropdownOpen
      });
   };

   render() {

const items =  this.props.list.map ((list) =>{           
    <DropdownItem>{list.name}</DropdownItem>
});
    
      return (
         
         <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
            <DropdownToggle caret className="btn-danger">Button Dropdown</DropdownToggle>
            <DropdownMenu>
           {items}
            </DropdownMenu>
         </ButtonDropdown>
      );
   }
}

export default ButtonsDropdown
