// import external modules
import React, { Component } from "react";
import { NavLink } from "react-router-dom";
// import axios from 'axios';
// import authService from '../../services/authService';
import Joi from "joi-browser";
import BlockUi from "react-block-ui";

import auth from "../../services/authService";



import {
   Row,
   Col,
   Input,
   Form,
   FormGroup,
   Button,
   Label,
   Card,   
   CardBody,
   CardFooter,
   // Alert
   // UncontrolledAlert
} from "reactstrap";




class Login extends Form {
   // constructor(props) {
   //    super(props);

   //    this.state = {
   //       isChecked: true,
   //       email: '',
   //       password: ''
   //    };
   // }

  

   // handleChecked = e => {
   //    this.setState(prevState => ({
   //       isChecked: !prevState.isChecked
   //    }));
   // };



  

  

state = {
    data: { username: "", password: "" },
    errors: {}
  };

  schema = {
    username: Joi.string()
      .required()
      .label("Username"),
    password: Joi.string()
      .required()
      .label("Password")
  };

  doSubmit = async () => {
    try {
      this.state.blocking = true;
      const { data } = this.state;
      await auth.login(data.username, data.password);
      this.state.blocking = false;

      window.location = "/dashboard";
    } catch (ex) {
      this.state.blocking = false;
      if (ex.response && ex.response.status === 401) {
        const errors = { ...this.state.errors };
        errors.username = ex.response.data.error;
        this.setState({ errors });
      }
    }
  };




   render() {
      return (
         <BlockUi tag="div" blocking={this.state.blocking}>
         <div className="container">
            <Row className="full-height-vh">
               <Col xs="12" className="d-flex align-items-center justify-content-center">
                  <Card className="gradient-indigo-purple text-center width-400">
                     <CardBody>
                        <h2 className="white py-4">Login</h2>
                        
        
          <form className="ui form" onSubmit={this.handleSubmit}>

         
          
                        {/* <Form className="pt-2" onSubmit={this.onFormSubmit}> */}
                     
                     
                        {/* <UncontrolledAlert
               color="danger"
               // isOpen={this.state.invisible}
               // toggle={this.onDismiss}
            >
               Email or Password incorrect
            </UncontrolledAlert> */}
           
                           <FormGroup>
                              <Col md="12">
                              <Input
                                    type="email"
                                    className="form-control"
                                    name="email"
                                    id="inputemail"
                                    placeholder="Email"
                                    value={this.state.email}
                                    onChange={(e) => this.setState({email : e.target.value})}
                                    required
                                 />
                              </Col>
                           </FormGroup>

                           <FormGroup>
                              <Col md="12">
                                 <Input
                                    type="password"
                                    className="form-control"
                                    name="password"
                                    id="inputPass"
                                    placeholder="Password"
                                    value={this.state.password}
                                    onChange={(e) => this.setState({password : e.target.value})}
                                    required
                                 />





                              </Col>
                           </FormGroup>

                           <FormGroup>
                              <Row>
                                 <Col md="12">
                                    <div className="custom-control custom-checkbox mb-2 mr-sm-2 mb-sm-0 ml-3">
                                       <Input
                                          type="checkbox"
                                          className="custom-control-input"
                                          checked={this.state.isChecked}
                                          onChange={this.handleChecked}
                                          id="rememberme"
                                       />
                                       <Label className="custom-control-label float-left white" for="rememberme">
                                          Remember Me
                                       </Label>
                                    </div>
                                 </Col>
                              </Row>
                           </FormGroup>
                           <FormGroup>
                              <Col md="12">
                                 <Button type="submit" color="danger" block className="btn-pink btn-raised">
                                    Login
                                 </Button>
                                 {/* <Button type="button" color="secondary" block className="btn-raised">
                                    Cancel
                                 </Button> */}
                              </Col>
                           </FormGroup>
                        {/* </Form> */}
                        </form>
                     </CardBody>
                     <CardFooter>
                        <div className="float-left">
                           <NavLink to="/pages/forgot-password" className="text-white">
                              Forgot Password?
                           </NavLink>
                        </div>
                        <div className="float-right">
                           
                        </div>
                     </CardFooter>
                  </Card>
               </Col>
            </Row>
         </div>
         </BlockUi>
      );
   }
}

export default Login;
